import{_ as f}from"./plugin-vue_export-helper.21dcd24c.js";import{b as m,o,f as a,F as p,q as c,n as v,h as t,t as s,z as h}from"./index.6c6610dc.js";const k={class:"cards-container"},y=["onClick"],x={class:"card-face card-front"},g={class:"card-content"},_={class:"definition"},b={class:"examples"},w={class:"card-face card-back"},S={class:"card-content"},I={class:"explanation"},T={__name:"pageInfoWordDifference",setup(F){const i=m([{id:1,front:{title:"Say vs. Tell",content:"You say something (a statement); you tell someone (a person + information).",examples:["\u201CShe said hello.\u201D","\u201CHe told me the news.\u201D"]},back:{explanation:"\xABSay\xBB \u2014 \u0434\u043B\u044F \u043F\u0435\u0440\u0435\u0434\u0430\u0447\u0438 \u0438\u043D\u0444\u043E\u0440\u043C\u0430\u0446\u0438\u0438; \xABTell\xBB \u0442\u0440\u0435\u0431\u0443\u0435\u0442 \u0443\u043A\u0430\u0437\u0430\u043D\u0438\u044F \u0441\u043B\u0443\u0448\u0430\u0442\u0435\u043B\u044F."}},{id:2,front:{title:"Travel vs. Trip",content:"Travel is the general act of going places; a trip is one specific journey.",examples:["\u201CI love to travel.\u201D","\u201CWe went on a weekend trip to Paris.\u201D"]},back:{explanation:"\xABTravel\xBB \u2014 \u043E\u0431\u0449\u0435\u0435 \u043F\u043E\u043D\u044F\u0442\u0438\u0435 \u043E \u043F\u0443\u0442\u0435\u0448\u0435\u0441\u0442\u0432\u0438\u044F\u0445; \xABTrip\xBB \u2014 \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u0430\u044F \u043F\u043E\u0435\u0437\u0434\u043A\u0430."}},{id:3,front:{title:"Listen vs. Hear",content:"Hear is passive (it happens automatically); Listen is active (you do it on purpose).",examples:["\u201CCan you hear that noise?\u201D","\u201CI\u2019m listening to music.\u201D"]},back:{explanation:"\xABHear\xBB \u2014 \u043F\u0430\u0441\u0441\u0438\u0432\u043D\u043E\u0435 \u0432\u043E\u0441\u043F\u0440\u0438\u044F\u0442\u0438\u0435 \u0437\u0432\u0443\u043A\u0430; \xABListen\xBB \u2014 \u0430\u043A\u0442\u0438\u0432\u043D\u043E\u0435 \u0432\u043D\u0438\u043C\u0430\u043D\u0438\u0435."}},{id:4,front:{title:"Each vs. Every",content:"Each focuses on individual items; every focuses on the group as a whole.",examples:["\u201CEach student got a book.\u201D","\u201CEvery student passed the test.\u201D"]},back:{explanation:"\xABEach\xBB \u2014 \u0430\u043A\u0446\u0435\u043D\u0442 \u043D\u0430 \u043E\u0442\u0434\u0435\u043B\u044C\u043D\u044B\u0445 \u044D\u043B\u0435\u043C\u0435\u043D\u0442\u0430\u0445; \xABEvery\xBB \u2014 \u043D\u0430 \u0433\u0440\u0443\u043F\u043F\u0435 \u0432 \u0446\u0435\u043B\u043E\u043C."}},{id:5,front:{title:"Speak vs. Talk",content:"Speak is more formal or one-way; Talk is casual and two-way.",examples:["\u201CMay I speak with the manager?\u201D","\u201CLet\u2019s talk after class.\u201D"]},back:{explanation:"\xABSpeak\xBB \u2014 \u0444\u043E\u0440\u043C\u0430\u043B\u044C\u043D\u043E\u0435 \u0438\u043B\u0438 \u043E\u0434\u043D\u043E\u0441\u0442\u043E\u0440\u043E\u043D\u043D\u0435\u0435 \u043E\u0431\u0449\u0435\u043D\u0438\u0435; \xABTalk\xBB \u2014 \u043D\u0435\u0444\u043E\u0440\u043C\u0430\u043B\u044C\u043D\u043E\u0435 \u0438 \u0434\u0432\u0443\u0441\u0442\u043E\u0440\u043E\u043D\u043D\u0435\u0435."}},{id:6,front:{title:"House vs. Home",content:"A house is the building; home is the feeling or personal space.",examples:["\u201CWe built a new house.\u201D","\u201CThis place feels like home.\u201D"]},back:{explanation:"\xABHouse\xBB \u2014 \u0437\u0434\u0430\u043D\u0438\u0435; \xABHome\xBB \u2014 \u043C\u0435\u0441\u0442\u043E \u0441 \u044D\u043C\u043E\u0446\u0438\u043E\u043D\u0430\u043B\u044C\u043D\u043E\u0439 \u043F\u0440\u0438\u0432\u044F\u0437\u0430\u043D\u043D\u043E\u0441\u0442\u044C\u044E."}},{id:7,front:{title:"Look vs. See",content:"See is automatic (your eyes work); look means you try to see.",examples:["\u201CLook at the sky!\u201D","\u201CI can see the stars.\u201D"]},back:{explanation:"\xABSee\xBB \u2014 \u0430\u0432\u0442\u043E\u043C\u0430\u0442\u0438\u0447\u0435\u0441\u043A\u043E\u0435 \u0437\u0440\u0435\u043D\u0438\u0435; \xABLook\xBB \u2014 \u043D\u0430\u043C\u0435\u0440\u0435\u043D\u043D\u043E\u0435 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435."}},{id:8,front:{title:"Fun vs. Funny",content:"Fun means enjoyable or entertaining; funny means it makes you laugh.",examples:["\u201CThe party was really fun.\u201D","\u201CThat joke was so funny!\u201D"]},back:{explanation:"\xABFun\xBB \u2014 \u0447\u0442\u043E-\u0442\u043E \u043F\u0440\u0438\u044F\u0442\u043D\u043E\u0435; \xABFunny\xBB \u2014 \u0447\u0442\u043E-\u0442\u043E \u0441\u043C\u0435\u0448\u043D\u043E\u0435."}},{id:9,front:{title:"Job vs. Work",content:"A job is a specific position (noun); Work is the general activity you do (noun or verb).",examples:["\u201CShe has a job at the bank.\u201D","\u201CI work every weekday.\u201D"]},back:{explanation:"\xABJob\xBB \u2014 \u043A\u043E\u043D\u043A\u0440\u0435\u0442\u043D\u0430\u044F \u0434\u043E\u043B\u0436\u043D\u043E\u0441\u0442\u044C; \xABWork\xBB \u2014 \u043E\u0431\u0449\u0430\u044F \u0434\u0435\u044F\u0442\u0435\u043B\u044C\u043D\u043E\u0441\u0442\u044C."}},{id:10,front:{title:"Borrow vs. Lend",content:"Borrow means you take something from someone; Lend means you give something to someone.",examples:["\u201CCan I borrow your pen?\u201D","\u201CI will lend you my pen.\u201D"]},back:{explanation:"\xABBorrow\xBB \u2014 \u0432\u0437\u044F\u0442\u044C \u0432\u0437\u0430\u0439\u043C\u044B; \xABLend\xBB \u2014 \u0434\u0430\u0442\u044C \u0432\u0437\u0430\u0439\u043C\u044B."}},{id:11,front:{title:"Still vs. Yet",content:"Still means something is continuing; Yet means something hasn\u2019t happened but is expected.",examples:["\u201CI\u2019m still waiting.\u201D","\u201CHe hasn\u2019t arrived yet.\u201D"]},back:{explanation:"\xABStill\xBB \u2014 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435 \u043F\u0440\u043E\u0434\u043E\u043B\u0436\u0430\u0435\u0442\u0441\u044F; \xABYet\xBB \u2014 \u0434\u0435\u0439\u0441\u0442\u0432\u0438\u0435 \u0435\u0449\u0451 \u043D\u0435 \u043D\u0430\u0447\u0430\u043B\u043E\u0441\u044C."}},{id:12,front:{title:"Since vs. For",content:"Since shows the starting point; For shows the length of time.",examples:["\u201CI\u2019ve lived here since 2020.\u201D","\u201CI\u2019ve lived here for 3 years.\u201D"]},back:{explanation:"\xABSince\xBB \u2014 \u043D\u0430\u0447\u0430\u043B\u043E \u043F\u0435\u0440\u0438\u043E\u0434\u0430; \xABFor\xBB \u2014 \u043F\u0440\u043E\u0434\u043E\u043B\u0436\u0438\u0442\u0435\u043B\u044C\u043D\u043E\u0441\u0442\u044C."}}]),d=l=>{const e=i.value.find(n=>n.id===l);e&&(e.isFlipped=!e.isFlipped)};return(l,e)=>(o(),a("div",k,[(o(!0),a(p,null,c(i.value,n=>(o(),a("div",{key:n.id,class:v(["card",{"is-flipped":n.isFlipped}]),onClick:r=>d(n.id)},[t("div",x,[t("div",g,[t("h3",null,s(n.front.title),1),e[2]||(e[2]=t("div",{class:"divider"},null,-1)),t("p",_,s(n.front.content),1),t("div",b,[(o(!0),a(p,null,c(n.front.examples,(r,u)=>(o(),a("p",{key:u,class:"example"},[e[0]||(e[0]=t("span",{class:"quote"},"\u201C",-1)),h(s(r),1),e[1]||(e[1]=t("span",{class:"quote"},"\u201D",-1))]))),128))]),e[3]||(e[3]=t("div",{class:"flip-hint"},"Click to flip \u2192",-1))])]),t("div",w,[t("div",S,[t("h3",null,s(n.front.title),1),e[4]||(e[4]=t("div",{class:"divider"},null,-1)),t("p",I,s(n.back.explanation),1),e[5]||(e[5]=t("div",{class:"flip-hint"},"\u2190 Click to return",-1))])])],10,y))),128))]))}};var C=f(T,[["__scopeId","data-v-ecd3d04a"]]);export{C as default};
